create table accountblocks (id int unsigned auto_increment primary key, uuid char(36), cronostamp datetime default null,passwordhash varchar(64),notes varchar(512));
CREATE TABLE `accountreqs` (`id` int(10) unsigned NOT NULL AUTO_INCREMENT,`uuid` char(36) DEFAULT NULL,`chronostamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,`avFirst` varchar(64) DEFAULT NULL,`avLast` varchar(64) DEFAULT NULL,`email` varchar(64) DEFAULT NULL,`RLfirst` varchar(64) DEFAULT NULL,`RLlast` varchar(64) DEFAULT NULL,`passwordhash` varchar(64) DEFAULT NULL,`salt` varchar(64) DEFAULT NULL,`notes` varchar(512) DEFAULT NULL,`status` char(15) DEFAULT NULL,`ipaddress` char(15) DEFAULT NULL,`profileImgThumb` text,PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;
CREATE TABLE `agreements` (`UserUUID` char(64) DEFAULT NULL,`policyver` decimal(4,2) DEFAULT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE `policies` (`policyver` decimal(4,2) DEFAULT NULL,`policytype` char(3) DEFAULT NULL,`policytext` longtext) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE `feeds` (`postparentid` char(36) DEFAULT NULL,`posterid` char(36) DEFAULT NULL,`postid` char(36) DEFAULT NULL,`postmarkup` text,`visibility` char(1) DEFAULT NULL,`comment` char(1) DEFAULT NULL,`commentlock` char(1) DEFAULT NULL,`editlock` char(1) DEFAULT NULL,`feedgroup` char(255) DEFAULT NULL,`chronostamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP) ENGINE=MyISAM DEFAULT CHARSET=latin1;
